import React from "react";
import { useParams } from "react-router-dom";
import { Container } from "reactstrap";
import HouseData from "../assets/data/HouseData";
import ReviewData from "../assets/data/ReviewData";
import CommonSection from "../components/UI/CommonSectionCommonSection";

const HouseDetails = () => {
  const { id } = useParams();
  const house = HouseData.find((h) => h.id === parseInt(id));

  if (!house) {
    return <p className="text-center mt-5">House not found.</p>;
  }

  const reviews = ReviewData.filter((review) => review.houseId === house.id);

  return (
    <div>
      <CommonSection title={house.name} />

      <Container className="my-5">
        <img
          src={house.imgUrl}
          alt={house.name}
          className="img-fluid rounded mb-4"
          style={{ height: "400px", width: "100%", objectFit: "cover" }}
        />

        <h4>Description</h4>
        <p>{house.description}</p>

        <h5 className="mt-4">Availability</h5>
        <p>{house.availability}</p>

        <h5 className="mt-4">Customer Reviews</h5>
        {reviews.length > 0 ? (
          reviews.map((r, index) => (
            <div key={index} className="mb-3 border-bottom pb-2">
              <strong>{r.name}</strong> - ⭐ {r.rating}/5
              <p>{r.comment}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet.</p>
        )}

        <div className="text-center mt-5">
          <button className="btn btn-success px-4 py-2">
            Book Now
          </button>
          <p className="mt-2 text-muted">
            We’ll contact you to discuss details.
          </p>
        </div>
      </Container>
    </div>
  );
};

export default HouseDetails;
