import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { Container } from "reactstrap";
import HouseData from "../assets/data/HouseData";
import ReviewData from "../assets/data/reviews";
import CommonSection from "../components/UI/CommonSection";

const HouseDetails = () => {
  const { id } = useParams();
  const house = HouseData.find((h) => h.id === parseInt(id));

  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  if (!house) {
    return <p className="text-center mt-5">House not found.</p>;
  }

  const reviews = ReviewData.filter((review) => review.houseId === house.id);

  return (
    <div>
      <CommonSection title={house.name} />

      <Container className="my-5">
        <img
          src={house.imgUrl}
          alt={house.name}
          className="img-fluid rounded mb-4"
          style={{ height: "400px", width: "100%", objectFit: "cover" }}
        />

        <h4>Description</h4>
        <p>{house.description}</p>

        <h5 className="mt-4">Check Availability</h5>
        <div className="d-flex gap-3 mb-3">
          <div>
            <label>Start Date:</label>
            <input
              type="date"
              className="form-control"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
          </div>
          <div>
            <label>End Date:</label>
            <input
              type="date"
              className="form-control"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </div>
        </div>
        {startDate && endDate && (
          <p>
            Selected stay: <strong>{startDate}</strong> to <strong>{endDate}</strong>.{" "}
            {house.availability ? "House is available!" : "House might not be available on these dates."}
          </p>
        )}

        <h5 className="mt-4">Customer Reviews</h5>
        {reviews.length > 0 ? (
          reviews.map((r, index) => (
            <div key={index} className="mb-3 border-bottom pb-2">
              <strong>{r.user}</strong> - ⭐ {r.rating}/5
              <p>{r.comment}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet.</p>
        )}

        <div className="text-center mt-5">
          <p className="text-muted">
            Contact us to book. We’ll get in touch to discuss details.
          </p>
        </div>
      </Container>
    </div>
  );
};

export default HouseDetails;
