import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { Container, Row, Col, Card, CardBody } from "reactstrap";
import HouseData from "../assets/data/HouseData";
import ReviewData from "../assets/data/";
import CommonSection from "../components/UI/CommonSection";

const HouseDetails = () => {
  const { id } = useParams();
  const house = HouseData.find((h) => h.id === parseInt(id));

  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  if (!house) {
    return <p className="text-center mt-5">House not found.</p>;
  }

  const reviews = ReviewData.filter((review) => review.houseId === house.id);

  return (
    <div>
      <CommonSection title={house.name} />

      <Container className="my-5">
        {/* House Image */}
        <img
          src={house.imgUrl}
          alt={house.name}
          className="img-fluid rounded mb-4 shadow-sm"
          style={{ height: "400px", width: "100%", objectFit: "cover" }}
        />

        {/* Description */}
        <h4 className="mb-3">Description</h4>
        <p className="text-muted">{house.description}</p>

        {/* Availability Section */}
        <h5 className="mt-5 mb-3">Check Availability</h5>
        <Row className="g-3 align-items-end">
          <Col md={3}>
            <label className="form-label">Start Date</label>
            <input
              type="date"
              className="form-control"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
          </Col>
          <Col md={3}>
            <label className="form-label">End Date</label>
            <input
              type="date"
              className="form-control"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </Col>
          <Col md={6}>
            {startDate && endDate && (
              <p className="mt-2">
                Selected stay:{" "}
                <strong>{startDate}</strong> to <strong>{endDate}</strong>.{" "}
                <span className={`fw-bold ${house.availability ? "text-success" : "text-danger"}`}>
                  {house.availability ? "House is available!" : "Availability not guaranteed."}
                </span>
              </p>
            )}
          </Col>
        </Row>

        {/* Reviews Section */}
        <h5 className="mt-5 mb-3">Customer Reviews</h5>
        {reviews.length > 0 ? (
          <Row className="g-4">
            {reviews.map((r) => (
              <Col md={6} key={r.id}>
                <Card className="shadow-sm h-100">
                  <CardBody>
                    <div className="d-flex align-items-center mb-2">
                      <div
                        className="rounded-circle bg-secondary text-white d-flex justify-content-center align-items-center me-3"
                        style={{ width: "50px", height: "50px", fontSize: "1rem" }}
                      >
                        {r.user.charAt(0)}
                      </div>
                      <div>
                        <strong>{r.user}</strong>
                        <div className="text-warning">{"⭐".repeat(r.rating)}</div>
                      </div>
                    </div>
                    <p className="mb-1">{r.comment}</p>
                    <small className="text-muted">{r.date}</small>
                  </CardBody>
                </Card>
              </Col>
            ))}
          </Row>
        ) : (
          <p>No reviews yet.</p>
        )}

        {/* Contact Section */}
        <div className="text-center mt-5">
          <p className="text-muted fs-5">
            Interested in this house? <strong>Contact us</strong> to book. We’ll get in touch to discuss details.
          </p>
        </div>
      </Container>
    </div>
  );
};

export default HouseDetails;
