import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { Container, Row, Col, Card, CardBody, CardTitle, CardText, Badge } from "reactstrap";
import HouseData from "../assets/data/HouseData";
import ReviewData from "../assets/data/ReviewData";
import CommonSection from "../components/UI/CommonSection";

const HouseDetails = () => {
  const { id } = useParams();
  const house = HouseData.find((h) => h.id === parseInt(id));

  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  if (!house) {
    return <p className="text-center mt-5">House not found.</p>;
  }

  const reviews = ReviewData.filter((review) => review.houseId === house.id);

  const availabilityMessage = () => {
    if (!startDate || !endDate) return "";
    return house.availability
      ? "✅ This house is available for your selected dates."
      : "⚠️ The house might not be available for your selected dates.";
  };

  return (
    <div>
      <CommonSection title={house.name} />

      <Container className="my-5">
        <Row>
          <Col lg="8">
            <Card className="mb-4 shadow-sm">
              <img
                src={house.imgUrl}
                alt={house.name}
                className="img-fluid rounded-top"
                style={{ height: "450px", width: "100%", objectFit: "cover" }}
              />
              <CardBody>
                <CardTitle tag="h4">{house.name}</CardTitle>
                <CardText>{house.description}</CardText>
              </CardBody>
            </Card>

            <Card className="mb-4 shadow-sm p-3">
              <h5>Check Availability</h5>
              <Row className="g-3 align-items-end">
                <Col md="6">
                  <label>Start Date:</label>
                  <input
                    type="date"
                    className="form-control"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                  />
                </Col>
                <Col md="6">
                  <label>End Date:</label>
                  <input
                    type="date"
                    className="form-control"
                    value={endDate}
                    onChange={(e) => setEndDate(e.target.value)}
                  />
                </Col>
              </Row>
              {startDate && endDate && (
                <p className={`mt-3 fw-bold ${house.availability ? "text-success" : "text-warning"}`}>
                  {availabilityMessage()}
                </p>
              )}
            </Card>

            <h5 className="mb-3">Customer Reviews</h5>
            {reviews.length > 0 ? (
              reviews.map((r) => (
                <Card key={r.id} className="mb-3 shadow-sm">
                  <CardBody>
                    <CardTitle tag="h6">
                      {r.user} <Badge color="warning" className="ms-2">⭐ {r.rating}/5</Badge>
                    </CardTitle>
                    <CardText>{r.comment}</CardText>
                    <small className="text-muted">{r.date}</small>
                  </CardBody>
                </Card>
              ))
            ) : (
              <p>No reviews yet.</p>
            )}

            <div className="text-center mt-5">
              <p className="text-muted fw-bold">
                Contact us to book. We’ll get in touch to discuss details.
              </p>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default HouseDetails;
